mkdir TOMCAT_WORK/kfs/reports/emailed; mkdir $HOME/bin; echo -e "#!/bin/bash
IND_DIR=TOMCAT_WORK/kfs/reports/emailed;
COUNT=0;
for FILE in \$@;
do if [ -s "\$FILE" ] && [ ! -e "\$IND_DIR/\$FILE.emailed" ];
then
 echo -n "-a \$FILE "
 touch "\$IND_DIR/\${FILE}.emailed"
 let COUNT+=1
fi
done
if [ "\$COUNT" -eq "0" ];
then
 \`kill -s INT \$\$\`
fi" > $HOME/bin/att.sh; chmod u+x $HOME/bin/att.sh

